// Global

const GSD = getWorkflowStaticData('global');

if ( GSD && GSD.message ){
  console.log(GSD);
} else {
  GSD.message = "Hello, Global Static Data";
}

// Local (node)

const LSD = getWorkflowStaticData('node');

if ( LSD && LSD.message ){
  console.log(LSD);
} else {
  LSD.message = "Hello, Node Static Data";
}

